export const getArticlesByCategory = (category) => {
  return articles.filter(article => article.category === category);
};

export const getArticleByUrl = (url) => {
  // Normalisiere beide URLs für den Vergleich
  const normalizedInputUrl = url.startsWith('/') ? url : '/' + url;
  const normalizedInputUrlWithoutSlash = url.startsWith('/') ? url.substring(1) : url;
  
  return articles.find(article => {
    // Versuche verschiedene URL-Formate für maximale Kompatibilität
    return article.url === url || 
           article.url === normalizedInputUrl || 
           article.url === normalizedInputUrlWithoutSlash ||
           article.url.substring(1) === normalizedInputUrlWithoutSlash;
  });
};

export const searchArticles = (query) => {
  const lowercaseQuery = query.toLowerCase();
  return articles.filter(article => 
    article.title.toLowerCase().includes(lowercaseQuery) ||
    article.excerpt.toLowerCase().includes(lowercaseQuery) ||
    article.content.toLowerCase().includes(lowercaseQuery)
  );
};
